import "bootstrap/dist/css/bootstrap.css";
import "./messagestyle.css";
import React from "react";

import { useContext } from "react";
import { SocketContext } from '../SocketContext';

import moment from "moment";

import { Button, TextField, Grid, Typography, Container, Paper } from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
const useStyles = makeStyles((theme) => ({
  root: {
    display: 'flex',
    flexDirection: 'column',
  },
  gridContainer: {
    justifyContent: 'center',
    width: '100%',
    [theme.breakpoints.down('xs')]: {
      flexDirection: 'row',
    },
  },
  container: {
    justifyContent: 'center',
    width: '600px',
    margin: '0px 0',
    padding: 0,
    [theme.breakpoints.down('xs')]: {
      width: '80%',
    }
  },
  
  margin: {
    marginTop: 20,
  },
  padding: {
    padding: 0,
  },
  paper: {
    justifyContent: 'center',
    padding: '0px 0px',
    border: '4px solid black',

  },
}));



const Message =({children}) =>{
    const {users,message,messages,submit,setMessage,username,user} =useContext(SocketContext);
    const classes = useStyles();
    return(/*
<div className="container" >
    <div className="row">
      <div className="col-md-12 mt-4 mb-4" >
        <h6>{username}</h6>
      </div>
    </div>
    <div className="row">
      <div className="col-md-8" >
        <h6>Messages</h6>
        <div id="messages" >
          {messages.map(({ user, date, text }, index) => (
            <div key={index} className="row mb-2">
              <div className="col-md-3" >
                <h5>{moment(date).format("h:mm:ss a")}</h5>
              </div>
              <div className="col-md-2"><h5>{user.name}</h5></div>
              <div className="col-md-2">{text}</div>
            </div>
          ))}
        </div>
        <form onSubmit={submit} id="form">
          <div className="input-group">
            <input
              type="text"
              className="form-control"
              onChange={e => setMessage(e.currentTarget.value)}
              value={message}
              id="text"
            />
            <span className="input-group-btn">
              <button id="submit" type="submit" className="btn btn-primary">
                Send
              </button>
            </span>
          </div>
        </form>
      </div>
      <div className="col-md-4">
        <h6>Users</h6>
        <ul id="users">
          {users.map(({ name, id }) => (
            <li key={id}>{name}</li>
          ))}
        </ul>
      </div>
    </div>
  </div>*/
  <Container className={classes.container}>
                             
                    <Paper elevation={10} className={classes.paper}>
                    <Grid container className={classes.gridContainer}>
                        <Grid item xs={12} md={6} className="row"></Grid>
                        <Typography variant="h4" >Messenger</Typography>
                            
                          
                            
                        <div id="messages" class="overflow-auto">
                            {messages.map(({ user, date, text }, index) => (
                              
                              <div key={index} className="row mb-3">
                                <div  className="col md-3"><h6><b>{user.name}</b></h6></div>
                                
                                <div className="col md-2" >
                                  <i>{moment(date).format("h:mma")}</i>
                                </div>
                                <div className="col md-3" >{text}</div>
                              </div>
                            ))}
                          </div>
                          <form onSubmit={submit} id="form">
                              <div className="input-group">
                                <input
                                  type="text"
                                  className="form-control" 
                                  onChange={e => setMessage(e.currentTarget.value)}
                                  value={message}
                                  id="text"
                                /><span className="input-group-btn">
                                <Button variant="contained" color="secondary" id="submit" type="submit"  onClick={submit} className={classes.margin}>
                                    Send
                                  </Button>
                                </span>
                                </div>
                                
                            </form>
                            
                              
                              
                          
                      </Grid>
                      
                      </Paper>
                      <Paper elevation={10} className={classes.paper}>
                      <div align="left" className="col-md-4" >
                                <h5>Users Online</h5>
                                <ul id="users">
                                  {users.map(({ name, id }) => (
                                    <li key={id}>{name}</li>
                                  ))}
                                </ul>
                              </div>
                      </Paper>
                      
                        
                      
                      </Container>

    )
}

export default Message
