import React, { useContext } from 'react';
import {Button, Grid, Typography, Paper } from '@material-ui/core';
import TextField from "@material-ui/core/TextField"
import { makeStyles } from '@material-ui/core/styles';

import { SocketContext } from '../SocketContext';

//import "./App.css"

/*const useStyles = makeStyles((theme) => ({
    .card: {
        display: grid,
        grid-template-columns: 1fr 1fr,
        margin: 30px,
        min-height: 30rem,
      }
      form: {
        max-width: 350px, 
        border-radius: 5px,
        padding: 20px,
        box-shadow: 0px 3px 24px -8px rgba(0, 0, 0, 0.75),
      }
      
      .name-field {
        margin-bottom: 40px;
      }
      
      button : {
        margin-top: 20px,
        padding: 10px,
        background: transparent,
        border-radius: 5px,
      }
      
      render-chat :{
        max-width: 350px,
        border-radius: 5px,
        padding: 20px,
        box-shadow: 0px 3px 24px -8px rgba(0, 0, 0, 0.75),
      }
      
      h3 {
        color: #2f72da,
      }
      
      span {
        color: black,
      }
    }))
*/

const Text = () => {
    const { name, state,chat,socketRef,onMessageSubmit,renderChat } = useContext(SocketContext);
    
    return(
<div className="card">
			<form onSubmit={onMessageSubmit}>
				<h1>Messenger</h1>
			
				<div>
					<TextField
						name="message"
						
						value={state.message}
						id="outlined-multiline-static"
						variant="outlined"
						label="Message"
					/>
				</div>
				<button>Send Message</button>
			</form>
			<div className="render-chat">
				<h1>Chat Log</h1>
				{renderChat()}
			</div>
</div>
)}

export default Text
